export const fetchRegionList = async () => {
           //Have a try and catch block for catching errors.
           try {
               //Assign the promise unresolved first then get the data using the json method.
               const regionApiCall = await fetch(`${APIConfig.API_URI}` + 'api/v1/region/list');
               const optionsRegion = await regionApiCall.json();

                 this.setState({
                      optionsRegion: optionsRegion
                 });
           } catch(err) {
               console.log("Error fetching region list -----------", err);
           }

}
